import React, {useState} from "react";
import styles from "./forrad.module.scss";
import Link from "next/link";

export default function index() {
  const [locationValue, setLocationValue] = useState(null);

  const router = useRouter();

  console.log(locationValue);

  let ready = styles.noneButton;
  if (locationValue) {
    ready = styles.button;
  }

  return (
    <main className={styles.main}>
      <div className={styles.wrapper}>
        <div className={styles.selectwrapper}>
          <h1 className={styles.h1}>Hyr Containerförråd</h1>
          <h3 className={styles.h3}>Välj en plats</h3>
          <select
            className={styles.select}
            name="location"
            onChange={(e) => {
              setLocationValue(e.target.value);
            }}
            value={locationValue}
          >
            <option value="null"></option>
            <option value="Kallarbacken">Västerås - Källarbacken</option>
            <option value="Nystrandsgatan">Eskilstuna - Nystrandsgatan</option>
          </select>
          <Link href="/container/[locationValue]" as={`/container/${locationValue}`}>
            <button className={ready}>Se tillgängliga förråd</button>
          </Link>
        </div>
        <div className={styles.containerImgWrapper}>
          <img className={styles.containerImg} src="static/images/container.svg" alt="SB Fastigheter logo" />
        </div>
      </div>
    </main>
  );
}
