import React, {useEffect, useState} from "react";
import styles from "./ContainerForm.module.scss";

let initialState = {
  fornamn: "",
  efternamn: "",
  email: "",
  personnummer: "",
  telefonnummer: "",
  stad: "",
  gatuAdress: "",
  postnummer: "",
  errors: {
    fornamn: false,
    efternamn: false,
    email: false,
    personnummer: false,
    telefonnummer: false,
    stad: false,
    gatuAdress: false,
    postnummer: false,
  },
};

export function ContainerForm() {
  const [state, setState] = useState(initialState);

  let form = {};

  const handleInputChange = (e) => {
    const target = e.target;
    const value = target.value;
    const name = target.name;

    const regexName = /^[a-öA-Ö]{2,21}$/;
    const regexEmail = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

    regexName.test(state.fornamn) ? (state.errors.fornamn = false) : (state.errors.fornamn = true);
    regexName.test(state.efternamn) ? (state.errors.efternamn = false) : (state.errors.efternamn = true);
    regexEmail.test(state.email) ? (state.errors.email = fasle) : (state.errors.email = true);

    setState({...state, [name]: value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div>
      <div className={styles.main}>
        <div className={styles.wrapper}>
          <div className={styles.formWrapper}>
            <form onSubmit={handleSubmit} className={styles.form}>
              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="fornamn">
                  Förnamn
                </label>
                <input
                  onChange={handleInputChange}
                  value={state.fornamn}
                  className={state.errors.fornamn ? styles.inputFail : styles.input}
                  placeholder=""
                  type="text"
                  name="fornamn"
                  id="fornamn"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="efternamn">
                  Efternamn
                </label>
                <input
                  onChange={handleInputChange}
                  value={state.efternamn}
                  className={state.errors.efternamn ? styles.inputFail : styles.input}
                  placeholder=""
                  type="text"
                  name="efternamn"
                  id="efternamn"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="email">
                  Email
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="" name="email" id="email"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="personnummer">
                  Personnummer
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="number" name="personnummer" id="personnummer"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="telefonnummer">
                  Telefonnummer
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="tel" name="telefonnummer" id="telefonnummer"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="stad">
                  Stad
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="text" name="stad" id="stad"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="gatuadress">
                  Gatuadress
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="text" name="gatuAdress" id="gatuadress"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="postnummer">
                  Postnummer
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="number" name="postnummer" id="postnummer"></input>
              </div>

              <div className={styles.inputWrapper}>
                <input className={styles.submit} type="submit" value="Skicka in" />
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
