import React, {useEffect, useState} from "react";
import styles from "./ContainerForm.module.scss";

let initialState = {
  fornamn: "",
  efternamn: "",
  email: "",
  personnummer: "",
  telefonnummer: "",
  stad: "",
  gatuAdress: "",
  postnummer: "",
  errors: {
    fornamn: false,
    efternamn: false,
    email: false,
    personnummer: false,
    telefonnummer: false,
    stad: false,
    gatuAdress: false,
    postnummer: false,
  },
};

export function ContainerForm() {
  const [state, setState] = useState(initialState);

  const handleInputChange = (e) => {
    const target = e.target;
    const value = target.value;
    const name = target.name;
    let error = false;

    let regex = "";

    switch (target.name) {
      case "fornamn":
        regex = /^[a-öA-Ö]{2,21}$/;
        break;
      case "efternamn":
        regex = /^[a-öA-Ö]{2,21}$/;
        break;
      case "email":
        regex = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        break;
      case "personnummer":
        regex = /^(\d{10}|\d{12})$/;
        break;
    }
    console.log(regex);
    regex.test(state[name]) ? (error = false) : (error = true);

    // setState({...state, errors[name]: error});
    console.log(state.errors[name]);
    setState({
      ...state,
      [name]: value,
      errors: {
        fornamn: state.errors.fornamn,
        efternamn: state.errors.efternamn,
        email: state.errors.email,
        personnummer: state.errors.personnummer,
        telefonnummer: state.errors.telefonnummer,
        stad: state.errors.stad,
        gatuAdress: state.errors.gatuAdress,
        postnummer: state.errors.postnummer,
        [name]: error,
      },
    });

    console.log(state);
  };

  const handleInputChangeForReal = (e) => {
    const target = e.target;
    const value = target.value;
    const name = target.name;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div>
      <div className={styles.main}>
        <div className={styles.wrapper}>
          <div className={styles.formWrapper}>
            <form onSubmit={handleSubmit} className={styles.form}>
              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="fornamn">
                  Förnamn
                </label>
                <input
                  onChange={handleInputChangeForReal}
                  onBlur={handleInputChange}
                  value={state.fornamn}
                  className={state.errors.fornamn ? styles.inputFail : styles.input}
                  type="text"
                  name="fornamn"
                  id="fornamn"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="efternamn">
                  Efternamn
                </label>
                <input onBlur={handleInputChange} className={state.errors.efternamn ? styles.inputFail : styles.input} type="text" name="efternamn" id="efternamn"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="email">
                  Email
                </label>
                <input onBlur={handleInputChange} className={state.errors.email ? styles.inputFail : styles.input} type="email" name="email" id="email"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="personnummer">
                  Personnummer
                </label>
                <input onBlur={handleInputChange} className={state.errors.personnummer ? styles.inputFail : styles.input} type="number" name="personnummer" id="personnummer"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="telefonnummer">
                  Telefonnummer
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="tel" name="telefonnummer" id="telefonnummer"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="stad">
                  Stad
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="text" name="stad" id="stad"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="gatuadress">
                  Gatuadress
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="text" name="gatuAdress" id="gatuadress"></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="postnummer">
                  Postnummer
                </label>
                <input onChange={handleInputChange} className={styles.input} placeholder="" type="number" name="postnummer" id="postnummer"></input>
              </div>

              <div className={styles.inputWrapper}>
                <input className={styles.submit} type="submit" value="Skicka in" />
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
