import React, {useEffect, useState} from "react";
import styles from "./ContainerForm.module.scss";
import {regexForm} from "../../utils/regexForm";

let initialState = {
  fornamn: "",
  efternamn: "",
  email: "",
  personnummer: "",
  telefonnummer: "",
  stad: "",
  gatuAdress: "",
  postnummer: "",
  errors: {
    fornamn: false,
    efternamn: false,
    email: false,
    personnummer: false,
    telefonnummer: false,
    stad: false,
    gatuAdress: false,
    postnummer: false,
  },
};

export function ContainerForm() {
  const [state, setState] = useState(initialState);

  const handleInputChange = (e) => {
    const target = e.target;
    const value = target.value;
    const name = target.name;

    setState({
      ...state,
      [name]: value,
      errors: {
        fornamn: state.errors.fornamn,
        efternamn: state.errors.efternamn,
        email: state.errors.email,
        personnummer: state.errors.personnummer,
        telefonnummer: state.errors.telefonnummer,
        stad: state.errors.stad,
        gatuAdress: state.errors.gatuAdress,
        postnummer: state.errors.postnummer,
      },
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div>
      <div className={styles.main}>
        <div className={styles.wrapper}>
          <div className={styles.formWrapper}>
            <form onSubmit={handleSubmit} className={styles.form}>
              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="fornamn">
                  FÃ¶rnamn
                </label>
                <input
                  onChange={handleInputChange}
                  onBlur={handleRegex}
                  value={state.fornamn}
                  className={state.errors.fornamn ? styles.inputFail : styles.input}
                  type="text"
                  name="fornamn"
                  id="fornamn"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="efternamn">
                  Efternamn
                </label>
                <input
                  onChange={handleInputChange}
                  onBlur={handleRegex}
                  value={state.efternamn}
                  className={state.errors.efternamn ? styles.inputFail : styles.input}
                  type="text"
                  name="efternamn"
                  id="efternamn"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="email">
                  Email
                </label>
                <input
                  onChange={handleInputChange}
                  onBlur={handleRegex}
                  value={state.email}
                  className={state.errors.email ? styles.inputFail : styles.input}
                  type="email"
                  name="email"
                  id="email"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="personnummer">
                  Personnummer
                </label>
                <input
                  onChange={handleInputChange}
                  onBlur={handleRegex}
                  value={state.personnummer}
                  className={state.errors.personnummer ? styles.inputFail : styles.input}
                  name="personnummer"
                  id="personnummer"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="telefonnummer">
                  Telefonnummer
                </label>
                <input
                  onChange={handleInputChange}
                  onBlur={handleRegex}
                  value={state.telefonnummer}
                  className={state.errors.telefonnummer ? styles.inputFail : styles.input}
                  name="telefonnummer"
                  id="telefonnummer"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="stad">
                  Stad
                </label>
                <input
                  onChange={handleInputChange}
                  onBlur={handleRegex}
                  value={state.stad}
                  className={state.errors.stad ? styles.inputFail : styles.input}
                  placeholder=""
                  type="text"
                  name="stad"
                  id="stad"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="gatuadress">
                  Gatuadress
                </label>
                <input
                  onChange={handleInputChange}
                  onBlur={handleRegex}
                  value={state.gatuAdress}
                  className={state.errors.gatuAdress ? styles.inputFail : styles.input}
                  type="text"
                  name="gatuAdress"
                  id="gatuadress"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <label className={styles.label} htmlFor="postnummer">
                  Postnummer
                </label>
                <input
                  onChange={handleInputChange}
                  onBlur={handleRegex}
                  value={state.postnummer}
                  className={state.errors.postnummer ? styles.inputFail : styles.input}
                  name="postnummer"
                  id="postnummer"
                ></input>
              </div>

              <div className={styles.inputWrapper}>
                <input className={styles.submit} type="submit" value="Skicka in" />
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
